this.killSelf = function(desc)
{
	if(desc!==null){
		player.consoleMessage(this.name+" - Check your Latest.log.",10);
		log(this.name,this.name+" - Shutting down."+desc);
	}
	for(var prop in this){if(prop!=='name' && prop!=='version') delete this[prop];}
	this.deactivated = true;
	return;
};
this.changeToN = function(element){element[0] = parseInt(element[0],null); element[1] = parseInt(element[1],null); element[2] = parseInt(element[2],null);};
this.playerWillSaveGame = function()
{
	missionVariables.bgs_mapStats = JSON.stringify(this.mapStats);
	this.guiScreenChanged = function(){missionVariables.bgs_mapStats = null; delete this.guiScreenChanged;}
};
this.alertConditionChanged = this.guiScreenChanged = this.missionScreenOpportunity = function()
{
	if(this.ini){
		this.doIni();
		this.checkDone();
	}
	delete this.alertConditionChanged;
	delete this.guiScreenChanged;
	delete this.missionScreenOpportunity;
};
// insert basic set
this.doIni = function()
{
	var cl=0,cp=0;
	for(var i=0;i<this.xmaps.areas.length;i++){
		if(this.xmaps.areas[i].gal!==cl){cl++; cp+=this.mapMax[this.xmaps.areas[i-1].gal][0];}
		if((this.mapStats[this.xmaps.areas[i].gal][0]&Math.pow(2,i-cp))) worldScripts["BGS-M"].addToLRC(this.xmaps.areas[i]);
	}
	cl=0; cp=0;
	for(var i=0;i<this.xmaps.lanes.length;i++){
		if(this.xmaps.lanes[i].gal!==cl){cl++; cp+=this.mapMax[this.xmaps.lanes[i-1].gal][1];}
		if((this.mapStats[this.xmaps.lanes[i].gal][1]&Math.pow(2,i-cp))) worldScripts["BGS-M"].addToLRC(this.xmaps.lanes[i]);
	}
	cl=0; cp=0;
	for(var i=0;i<this.xmaps.specials.length;i++){
		if(this.xmaps.specials[i].gal!==cl){cl++; cp+=this.mapMax[this.xmaps.specials[i-1].gal][2];}
		if((this.mapStats[this.xmaps.specials[i].gal][2]&Math.pow(2,i-cp))){
			// Check requirement
			if(this.xmaps.specials[i].hasOwnProperty("check") && !worldScripts[this.xmaps.specials[i].check]) continue;
			worldScripts["BGS-M"].addToLRCSpecial(this.xmaps.specials[i]);
		}
	}
	delete this.ini;
	delete this.doIni;
	return;
};
// check current galaxy
this.checkMissing = function(what)
{
	var max = Math.pow(2,this.mapMax[galaxyNumber][what])-1,cur = this.mapStats[galaxyNumber][what];
	var r = this.helper.baseChange(max-cur,2);
	for(var i=r.length;i>=0;i--){
		if(r[i]==="1") return(r.length-i);
	}
	return(0);
};
// get entry
this.getSpecific = function(t,w,g)
{
	var r,c=0;
	switch(t){
		case 0: r = "areas"; break;
		case 1: r = "lanes"; break;
		case 2: r = "specials"; break;
	}
	for(var i=0;i<this.xmaps[r].length;i++){
		if(this.xmaps[r][i].gal!==g) continue;
		if(w===c) return(this.xmaps[r][i]);
		c++;
	}
	return(0);
};
this.shipDockedWithStation = function()
{
	if(this.ini || this.block>clock.days) return;
	if(system.isInterstellarSpace || system.sun.isGoingNova || system.sun.hasGoneNova) return;
	var q = Math.floor(Math.random()*3);
	var c = this.checkMissing(q);
	if(c){
		var en = this.getSpecific(q,c-1,galaxyNumber);
		if(en.hasOwnProperty("sn")) this.buildSnoopersMessage(en,c);
		else if(en.hasOwnProperty("direct")){
			switch(q){
				case 0: worldScripts["BGS-M"].addToLRC(en); break;
				case 1: worldScripts["BGS-M"].addToLRC(en); break;
				case 2: worldScripts["BGS-M"].addToLRCSpecial(en); break;
			}
			this.mapStats[galaxyNumber][q] |= Math.pow(2,c-1);
		}
	}
	return;
};
this.shipLaunchedFromStation = function()
{
	if(this.logging) log(this.name,"Current galaxy bitmask: "+this.mapStats[galaxyNumber]+" Next date: "+(this.block+1));
};
// sum xy
this.getCPX = function(element){this.p.x += element;};
this.getCPY = function(element){this.p.y += element;};
this.getCPL = function(element){this.p.y += element[1]; this.p.x += element[0];};
// prepare Snoopers message
this.buildSnoopersMessage = function(obj,c)
{
	var a = ["One","Two","Three","Four","Five","Six","Seven","Eight"],m = "Map-Update #"+(obj.gal+1);
	this.p = new Vector3D([0,0,0]);
	switch(obj.type){
		case 1:
			m += this.helper.strLZZ(c)+"0000";
			this.p.x = obj.posx;
			this.p.y = obj.posy;
			break;
		case 2:
			m += this.helper.strLZZ(c)+"0000";
			this.p.x = obj.posx;
			this.p.y = obj.posy;
			break;
		case 3:
			m += this.helper.strLZZ(c)+"0000";
			obj.ax.forEach(this.getCPX,this);
			this.p.x /= obj.ax.length;
			obj.ay.forEach(this.getCPY,this);
			this.p.y /= obj.ay.length;
			break;
		case 4:
			m += "0000"+this.helper.strLZZ(c);
			obj.pos.forEach(this.getCPL,this);
			this.p.x /= obj.pos.length;
			this.p.y /= obj.pos.length;
			break;
	}
	m += ": The Galactic Co-operative of Worlds has just officially christened a new "+(obj.type>3?"space lane":"area");
	m += " in Sector "+a[obj.gal]+" and Nav-Computers are receiving a immediate update via Hyperspeed Secure Comms on all GALCOP stations.\n\n";
	m += "The '"+obj.ov.toUpperCase().slice(19,obj.ov.length-4).replace(/_/g," ");
	if(obj.sn===1) m += "'s center is located "+this.p.distanceTo([51.2,25.6,0]).toFixed(2)+" LYs to the "+this.helper.mapCoordsDirection(this.p,[51.2,25.6,0])+" of the Sectors center.";
	else m += "is one of the main "+(obj.type>3?"space lane":"area")+"s in Sector "+a[obj.gal]+" under control of the Galactic Co-operative of Worlds.";
	m += " Commanders are advised to upgrade their ships systems to take advantage of these features.";
	var snc = worldScripts.snoopers.insertNews({ID:this.name,Message:m,Priority:2,Pic:"snoopers_gcnewsA14.png",Model:"bgs-xload",Pos:[4,3,14]});
	if(!snc) this.block = clock.days+this.helper.randSpan(this.minInter,this.maxInter);
	if(this.logging) log(this.name,"Snoopers returned: "+snc);
	return;
};
// Snoopers callback
this.newsDisplayed = function(which)
{
	var a = which.slice(12);
	var gal = parseInt(a[0],null)-1,tp = parseFloat(a.slice(2)),t,w;
	if(gal!==galaxyNumber){
		player.commsMessage("Map-Update failed!",10);
		return;
	}
	if(tp>9999){t = 0; w = parseFloat(a.slice(2,4))-1;}
	else if(tp>99){t = 2; w = parseFloat(a.slice(4,6))-1;}
	else {t = 1; w = parseFloat(a.slice(6,8))-1;}
	var ins = this.getSpecific(t,w,gal);
	if(ins.hasOwnProperty("type")){
		if(ins.sn!==1){
			var p=["areas","lanes","specials"],cur,s=0,cl=0;
			for(var i=0;i<this.xmaps[p[t]].length;i++){
				cur = this.xmaps[p[t]][i];
				if(cur.gal!==galaxyNumber) cl++;
				if(cur.sn===ins.sn){
					worldScripts["BGS-M"].addToLRC(cur);
					s += Math.pow(2,i-cl);
				}
			}
			this.mapStats[galaxyNumber][t] |= s;
		} else {
			worldScripts["BGS-M"].addToLRC(ins);
			this.mapStats[galaxyNumber][t] |= Math.pow(2,w);
		}
		this.block = clock.days+this.helper.randSpan(this.minInter,this.maxInter);
		if(this.logging) this.block = 1;
		this.checkDone();
	}
	return;
};
this.checkDone = function()
{
	var a = 0;
	for(var j=0;j<8;j++){
		if(this.mapStats[j][0]===Math.pow(2,this.mapMax[j][0])-1) this.jobDone[j] |= Math.pow(2,0);
		if(this.mapStats[j][1]===Math.pow(2,this.mapMax[j][1])-1) this.jobDone[j] |= Math.pow(2,1);
		if(this.mapStats[j][2]===Math.pow(2,this.mapMax[j][2])-1) this.jobDone[j] |= Math.pow(2,2);
		a += this.jobDone[j];
	}
	if(this.logging) log(this.name,"a:"+a+" "+this.jobDone);
	if(a===56) this.killSelf(null); //silent kill, job is completely done
	return;
};
